--
-- Структура таблицы `prose`
--

CREATE TABLE `prose` (
  `id` int(11) NOT NULL,
  `title` varchar(255) NOT NULL,
  `article` text NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

--
-- Дамп данных таблицы `prose`
--

INSERT INTO `prose` (`id`, `title`, `article`) VALUES
(1, 'Я помню чудное мгновенье', 'Я помню чудное мгновенье:\r\nПередо мной явилась ты,\r\nКак мимолетное виденье,\r\nКак гений чистой красоты.\r\n\r\nВ томленьях грусти безнадежной,\r\nВ тревогах шумной суеты,\r\nЗвучал мне долго голос нежный\r\nИ снились милые черты.\r\n\r\nШли годы. Бурь порыв мятежный\r\nРассеял прежние мечты,\r\nИ я забыл твой голос нежный,\r\nТвои небесные черты.\r\n\r\nВ глуши, во мраке заточенья\r\nТянулись тихо дни мои\r\nБез божества, без вдохновенья,\r\nБез слез, без жизни, без любви.'),
(2, 'Я вас любил, любовь еще, быть может', 'Я вас любил: любовь еще, быть может,\r\nВ душе моей угасла не совсем;\r\nНо пусть она вас больше не тревожит;\r\nЯ не хочу печалить вас ничем.\r\nЯ вас любил безмолвно, безнадежно,\r\nТо робостью, то ревностью томим;\r\nЯ вас любил так искренно, так нежно,\r\nКак дай вам бог любимой быть другим.'),
(3, 'Письмо Татьяны к Онегину:', 'Я к вам пишу – чего же боле?\r\nЧто я могу еще сказать?\r\nТеперь, я знаю, в вашей воле\r\nМеня презреньем наказать.\r\nНо вы, к моей несчастной доле\r\nХоть каплю жалости храня,\r\nВы не оставите меня.\r\nСначала я молчать хотела;\r\nПоверьте: моего стыда\r\nВы не узнали б никогда,\r\nКогда б надежду я имела\r\nХоть редко, хоть в неделю раз\r\nВ деревне нашей видеть вас,\r\nЧтоб только слышать ваши речи,\r\nВам слово молвить, и потом\r\nВсе думать, думать об одном\r\nИ день и ночь до новой встречи.\r\nНо, говорят, вы нелюдим;\r\nВ глуши, в деревне всё вам скучно,\r\nА мы… ничем мы не блестим,\r\nХоть вам и рады простодушно.\r\n\r\nЗачем вы посетили нас?\r\nВ глуши забытого селенья\r\nЯ никогда не знала б вас,\r\nНе знала б горького мученья.\r\nДуши неопытной волненья\r\nСмирив со временем (как знать?),\r\nПо сердцу я нашла бы друга,\r\nБыла бы верная супруга\r\nИ добродетельная мать.'),
(4, 'Талисман:', 'Там, где море вечно плещет\r\nНа пустынные скалы,\r\nГде луна теплее блещет\r\nВ сладкий час вечерней мглы,\r\nГде, в гаремах наслаждаясь,\r\nДни проводит мусульман,\r\nТам волшебница, ласкаясь,\r\nМне вручила талисман.\r\n\r\nИ, ласкаясь, говорила:\r\n«Сохрани мой талисман:\r\nВ нем таинственная сила!\r\nОн тебе любовью дан.\r\nОт недуга, от могилы,\r\nВ бурю, в грозный ураган,\r\nГоловы твоей, мой милый,\r\nНе спасет мой талисман.\r\n\r\nИ богатствами Востока\r\nОн тебя не одарит,\r\nИ поклонников пророка\r\nОн тебе не покорит;\r\nИ тебя на лоно друга,\r\nОт печальных чуждых стран,\r\nВ край родной на север с юга\r\nНе умчит мой талисман…'),
(5, 'Сожженное письмо', 'Прощай, письмо любви! прощай: она велела.\r\nКак долго медлил я! как долго не хотела\r\nРука предать огню все радости мои!..\r\nНо полно, час настал. Гори, письмо любви.\r\nГотов я; ничему душа моя не внемлет.\r\nУж пламя жадное листы твои приемлет…\r\nМинуту!.. вспыхнули! пылают — легкий дым\r\nВиясь, теряется с молением моим.\r\nУж перстня верного утратя впечатленье,\r\nРастопленный сургуч кипит… О провиденье!\r\nСвершилось! Темные свернулися листы;\r\nНа легком пепле их заветные черты\r\nБелеют… Грудь моя стеснилась. Пепел милый,\r\nОтрада бедная в судьбе моей унылой,\r\nОстанься век со мной на горестной груди…'),
(6, 'Цветок', 'Цветок засохший, безуханный,\r\nЗабытый в книге вижу я;\r\nИ вот уже мечтою странной\r\nДуша наполнилась моя:\r\n\r\nГде цвел? когда? какой весною?\r\nИ долго ль цвел? и сорван кем,\r\nЧужой, знакомой ли рукою?\r\nИ положен сюда зачем?\r\n\r\nНа память нежного ль свиданья,\r\nИли разлуки роковой,\r\nИль одинокого гулянья\r\nВ тиши полей, в тени лесной?\r\n\r\nИ жив ли тот, и та жива ли?\r\nИ нынче где их уголок?\r\nИли уже они увяли,\r\nКак сей неведомый цветок?');

